import React, { useState } from "react";
import { omit } from "lodash";
import "../EmployeeComponent/ListEmployee.css";

const useForm = () => {
  const [values, setValues] = useState({});
  const [errors, setErrors] = useState({});

  const handleChange = (event) => {
    event.persist();

    let name = event.target.name;
    let val = event.target.value;

    validate(event, name, val);

    setValues({
      ...values, // spread operator to store old values
      [name]: val,
    });
  };

  const validate = (event, name, values) => {
    switch (name) {
      case "firstName":
        if (values.length <= 4) {
          setErrors({
            ...errors,
            firstName: "first name atleast have 3 letters",
          });
        } else {
          console.log(event.target.value);
          let newObj = omit(errors, "firstName");
          setErrors(newObj);
        }
        break;

      case "lastName":
        if (values.length <= 3) {
          setErrors({
            ...errors,
            lastName: "last name atleast have 3 letters",
          });
        } else {
          let newObj = omit(errors, "lastName");
          setErrors(newObj);
        }
        break;

      case "userName":
        if (
          !new RegExp(
            /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
          ).test(values)
        ) {
          setErrors({
            ...errors,
            userName: "Enter a valid email address",
          });
        } else {
          let newObj = omit(errors, "userName");
          setErrors(newObj);
        }
        break;

      case "password":
        if (
          !new RegExp(/^(?=.*?[A-Z])(?=.*?[a-z])(?=.*?[0-9]).{8,}$/).test(
            values
          )
        ) {
          setErrors({
            ...errors,
            password:
              "Password should contains atleast 8 charaters and containing uppercase,lowercase and numbers",
          });
        } else {
          let newObj = omit(errors, "password");
          setErrors(newObj);
        }
        break;

        case "password1":

      default:
        console.log("default");
        break;
    }
  };

  return {
    values,
    errors,
    handleChange,
  };
};

export default useForm;
